    int main(void){
        int i, k;
        SeqListType SL;         //定义顺序表变量
        DATA data, *data1;      //定义结点保存数据类型变量和指针变量
        char key[15];           //保存关键字

        SeqListInit(&SL);       //初始化数据表

        do{
            printf("请输入学号 姓名 年龄: ");
