            scanf("%s %s %d", &data.key, &data.name, &data.age);
            if(data.age){       //年龄不是0 退出循环
                if(!SeqListAdd(&SL, data)){//添加元素到顺序表
                    break;      //当添加失败 退出循环
                }
            }else{              //当年龄为0 退出循环
                break;
            }
        }while(1);

        printf("顺序表为： \n");
        SeqListAll(&SL);

        while(1){
            fflush(stdin);
            printf("\n\n输入操作\n1.获取结点位置元素\t2.内容查询\t3.添加\t4.插入\t5.删除\t6.求长度\t7.遍历\t8.退出\n：");
            scanf("%d", &k);
            if(k == 8){
                break;
            }
            switch(k){
                case 1:
                    printf("输入元素位置：");
                    scanf("%d", &i);
                    data1 = SeqListFindByNum(&SL, i);
                    printf("元素为：(%s %s %d) \n", data1->key, data1->name, data1->age);
                    break;
                case 2:
                    printf("输入元素key(学号)：");
                    scanf("%s", &key);
                    i = SeqListFindByCont(&SL, key);
                    if(i == 0){
                        printf("没有找到对应元素！");
                        break;
                    }
                    data1 = SeqListFindByNum(&SL, i);
                    printf("位置为： %d ,元素为：(%s %s %d) \n", i, data1->key, data1->name, data1->age);
                    break;
                case 3:
                    printf("输入元素内容：");
                    scanf("%s %s %d", &data.key, &data.name, &data.age);
                    SeqListAdd(&SL, data);
                    break;
                case 4:
                    printf("输入位置和元素内容：");
                    scanf("%d %s %s %d", &i, &data.key, &data.name, &data.age);
                    SeqListInsert(&SL, i, data);
                    break;
                case 5:
                    printf("输入要删除的位置：");
                    scanf("%d", &i);
                    SeqListDelete(&SL, i);
                    break;
                case 6:
                    printf("-----%d------\n", SeqListLength(&SL));
                    break;
                case 7:
                    SeqListAll(&SL);
                     break;
            }
        }

        return 0;
    }


