        ChainBinTree *initRoot(){
            ChainBinTree *node;

            if(node = (ChainBinTree *)malloc(sizeof(ChainBinTree))){
                printf("\n输入根数据结点");
                scanf("%s", &node->data);
                node->left = NULL;
                node->right = NULL;

                return binTreeInit(node);
            }

            return NULL;
