            q->head = q->tail = 0;
        }

        int QueueIsEmpty(SeqQueue q)
        {
            return q.head = q.tail;
        }

        int QueueIn(SeqQueue *q, int n)
        {
            if((q->tail + 1) % q->head != 0)
            {
                q->Data[q->tail] = n;
                q->tail = (q->tail + 1) % QUEUE_MAXSIZE;
                return 1;
            }
            else
            {
                return 0;
            }
        }

        int QueueOut(SeqQueue *q, int *ch)
        {
            if(q->head != q->tail)
            {
                *ch = q->Data[q->head];
                q->head = (q->head + 1) % QUEUE_MAXSIZE;
                return 1;
            }
            else
            {
                return 0;
            }
        }

