    public class Xpath{
        @Test
        public void read() throws DocumentException {
            SAXReader reader = new SAXReader();//解析器
            Document document = reader.read(new File("src/book.xml"));//解析

            String s = document.selectSingleNode("//作者").getText();//得到第一个作者的内容
