        public class DBUtil {

            //获取数据库连接
            private static Connection getConnection() {
                try {
                    Class.forName("com.mysql.jdbc.Driver");

                    Connection connection = DriverManager.getConnection("jdbc:mysql://127.0.0.1:3306/test", "root", "fangshuoit");

                    return connection;
